import React, { useState } from "react";

import "./App.css";

import Expenses from "./components/Expenses/Expenses";
import NewExpense from "./components/NewExpense/NewExpense";

const App = () => {
  const expense = [
    {
      id: "e1",
      title: "Toilet Paper",
      amount: 94.12,
      date: new Date(2020, 7, 25),
    },
    { id: "e2", title: "New TV", amount: 799.59, date: new Date(2021, 2, 12) },
    {
      id: "e3",
      title: "Car Insurance",
      amount: 294.67,
      date: new Date(2021, 2, 28),
    },
    {
      id: "e4",
      title: "New Desk (Wooden)",
      amount: 450,
      date: new Date(2021, 5, 12),
    },
    {
      id: "e4",
      title: "New Desk (Wooden)",
      amount: 450,
      date: new Date(2021, 5, 12),
    },
  ];

  const [array, setArray] = useState("");

  const updateArray = (expense) => {
    setArray(expense);
  };

  const addExpenseHandler = (expense) => {
    console.log("In App.js");
    console.log(expense);
  };

  const addFilterYear = (year) => {
    console.log("Year in App.js");
    console.log(year);
  };

  return (
    <div className="container">
      <NewExpense onAddExpense={addExpenseHandler} />
      <Expenses item={updateArray} filteredYear={addFilterYear} />
    </div>
  );
};

export default App;
